(12, 'MR00003', '[{\"mb_sid\":17,\"mb_isbn\":9789571360706,\"mb_name\":\"老師，你會不會回來\",\"mb_author\":\"王政忠 \",\"mb_categories\":\"語言學習\",\"mb_remarks\":\"沒有汙點，有用筆畫筆記\",\"mb_publishing\":\"時報出版\",\"mb_publishDate\":\"2014-10-05T16:00:00.000Z\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"320\",\"mb_page\":\"336\",\"mb_savingStatus\":\"B尚可\",\"mb_pic\":\"17-01.jpg,17-02.jpg,17-03.jpg\",\"mb_status\":\"0\",\"mb_shelveMember\":\"MR00004\",\"mb_pairedMember\":null,\"mb_successDate\":\"0000-00-00\",\"mb_shelveDate\":\"2019-05-20T16:00:00.000Z\"},{\"mb_sid\":8,\"mb_isbn\":9789863611073,\"mb_name\":\"富爸爸財富執行力\",\"mb_author\":\"羅勃特‧T\",\"mb_categories\":\"醫療保健\",\"mb_remarks\":\"有汙點，有用筆畫筆記\",\"mb_publishing\":\"高寶\",\"mb_publishDate\":\"2015-03-24T16:00:00.000Z\",\"mb_version\":\"第三版\",\"mb_fixedPrice\":\"400\",\"mb_page\":\"432\",\"mb_savingStatus\":\"C欠佳\",\"mb_pic\":\"08-01.jpg,08-02.jpg,08-03.jpg\",\"mb_status\":\"0\",\"mb_shelveMember\":\"MR00002\",\"mb_pairedMember\":null,\"mb_successDate\":\"0000-00-00\",\"mb_shelveDate\":\"2019-08-24T16:00:00.000Z\"},{\"mb_sid\":7,\"mb_isbn\":9787508695778,\"mb_name\":\"ETF投資聖經\",\"mb_author\":\"王延巍\",\"mb_categories\":\"心理勵志\",\"mb_remarks\":\"沒有汙點，沒有畫線註記\",\"mb_publishing\":\"中信出版社\",\"mb_publishDate\":\"2018-10-31T16:00:00.000Z\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"354\",\"mb_page\":\"272\",\"mb_savingStatus\":\"A良好\",\"mb_pic\":\"07-01.jpg,07-02.jpg,07-03.jpg\",\"mb_status\":\"0\",\"mb_shelveMember\":\"MR00002\",\"mb_pairedMember\":null,\"mb_successDate\":\"0000-00-00\",\"mb_shelveDate\":\"2019-08-15T16:00:00.000Z\"},{\"mb_sid\":9,\"mb_isbn\":9789866278761,\"mb_name\":\"金融大騙局\",\"mb_author\":\"林育詩、葉佛和\",\"mb_categories\":\"飲食\",\"mb_remarks\":\"沒有汙點，有用筆畫筆記\",\"mb_publishing\":\"布克文化 \",\"mb_publishDate\":\"2013-04-26T16:00:00.000Z\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"280\",\"mb_page\":\"240\",\"mb_savingStatus\":\"B尚可\",\"mb_pic\":\"09-01.jpg,09-02.jpg,09-03.jpg\",\"mb_status\":\"0\",\"mb_shelveMember\":\"MR00002\",\"mb_p