(13, 'MR00166', '[{\"mb_sid\":31,\"mb_isbn\":9789865728601,\"mb_name\":\"幸福的起點：一個人，不寂寞\",\"mb_author\":\"劉凱西\",\"mb_categories\":\"心理勵志\",\"mb_remarks\":\"\",\"mb_publishing\":\"布克文化\",\"mb_publishDate\":\"2015-10-03\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"280\",\"mb_page\":\"264\",\"mb_savingStatus\":\"良好\",\"mb_pic\":\"31-01.jpg, 31-02.jpg, 31-03.jpg\",\"mb_status\":null,\"mb_shelveMember\":\"MR00004\",\"mb_pairedMember\":null,\"mb_successDate\":null,\"mb_shelveDate\":\"2019-11-17T16:00:00.000Z\"},{\"mb_sid\":7,\"mb_isbn\":9787508695778,\"mb_name\":\"ETF投資聖經\",\"mb_author\":\"王延巍\",\"mb_categories\":\"心理勵志\",\"mb_remarks\":\"沒有汙點，沒有畫線註記\",\"mb_publishing\":\"中信出版社\",\"mb_publishDate\":\"2018-11-01\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"354\",\"mb_page\":\"272\",\"mb_savingStatus\":\"A良好\",\"mb_pic\":\"07-01.jpg,07-02.jpg,07-03.jpg\",\"mb_status\":\"0\",\"mb_shelveMember\":\"MR00002\",\"mb_pairedMember\":null,\"mb_successDate\":\"0000-00-00\",\"mb_shelveDate\":\"2019-08-15T16:00:00.000Z\"},{\"mb_sid\":15,\"mb_isbn\":9789867283771,\"mb_name\":\"哈利波特：穿越魔法史\",\"mb_author\":\"林靜華\",\"mb_categories\":\"輕小說\",\"mb_remarks\":\"沒有汙點，沒有用筆畫筆記\",\"mb_publishing\":\"皇冠\",\"mb_publishDate\":\"2018-01-29\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"549\",\"mb_page\":\"144\",\"mb_savingStatus\":\"A良好\",\"mb_pic\":\"15-01.jpg,15-02.jpg,15-03.jpg\",\"mb_status\":\"3\",\"mb_shelveMember\":\"MR00003\",\"mb_pairedMember\":null,\"mb_successDate\":\"0000-00-00\",\"mb_shelveDate\":\"2019-04-22T16:00:00.000Z\"},{\"mb_sid\":30,\"mb_isbn\":9789577411860,\"mb_name\":\"歡迎哭泣\",\"mb_author\":\"橘子\",\"mb_categories\":\"文學小說\",\"mb_remarks\":\"無畫線註記\",\"mb_publishing\":\"春天\",\"mb_publishDate\":\"2019-01-30\",\"mb_version\":\"初版\",\"mb_fixedPrice\":\"280\",\"mb_page\":\"256\",\"mb_savingStatus\":\"A良好\",\"mb_pic\":\"30-01.jpg,30-02.jpg,30-03.jpg\",\"mb_status\":null,\"mb_shelveMember\":\"MR00004\",\"mb_pairedMember\":null,\"mb_successDate\":null,\"mb_shelveDate\":\"2019-11-14T16:00:00.000